import { useState } from "react";
const ShowOnscreen=(props)=>{
    const [ buy,setbuy]=useState(props)
    const [totaldata,settotaldata]=useState()


    console.log('imon hsowcreen',props)
    const handleBuy=(event)=>{
        event.preventDefault()
        
        
        const enterd_num = document.getElementById('in')
        console.log(enterd_num.value,buy)
        const updatedquanity=(buy.Quantity-parseInt(enterd_num.value)).toString()
        setbuy(props=>({
            ...props,Quantity:updatedquanity
        }))
        settotaldata(buy=>({
            ...totaldata,buy
        }))

    }
    const hndleDel=()=>{

    }
    return(
        totaldata.map((ele,i)=>{

        
            <form>
                <p>{ele.Name}</p>
                <p> Rs.{ele.Price}</p>
                <p>{ele.Quantity}KG</p>
                <label htmlFor="in"></label>
                <input type='number' id='in'></input>
                <button type='click' onClick={handleBuy}>Buy</button>
                <button type='click' onClick={hndleDel}>Delete</button>
            </form>
        })
    )

}
export default ShowOnscreen
