import { useState } from "react"
import ShowOnscreen from "./ShowOnScreen"

const Form=()=>{
    const [entereddata,setentereddata]=useState('')
    const [submitted,setsubmitted]=useState(false)
    const [totaldata,settotaldata]= useState([])
    const handleFormSubmit=(event)=>{
        event.preventDefault()
        // setentereddata(event)
        const form_data = {
            Name:event.target.elements.name.value,
            Price: event.target.elements.price.value,
            Quantity:event.target.elements.quantity.value

        }
        setentereddata(form_data)
        setsubmitted(true)
        settotaldata([...totaldata,form_data])
        console.log(totaldata)

    }
    return(
        <div>
        
            <form onSubmit={handleFormSubmit}>   
                <h1>Vegetable Shop</h1>     
                <label htmlFor="name">Name</label>
                <input type='text' id='name'></input>
                <label htmlFor="price">Price</label>
                <input type='number' id='price'></input>
                <label htmlFor="quantity">Quantity</label>
                <input type="number" id='quantity'></input>
                <button type='submit'>Add to Shop</button>
                
            </form>
            {submitted && <ShowOnscreen {...entereddata} {...totaldata}/>}
            
        </div>
    )
}
export default Form
