import { useState } from "react";

const ShowOnscreen = (props) => {
    const [buy, setbuy] = useState(props);
    const [totaldata, settotaldata] = useState([]);

    console.log('imon hsowcreen', props);

    const handleBuy = (event) => {
        event.preventDefault();
        const enteredNum = parseInt(document.getElementById('in').value);
        const updatedQuantity = buy.Quantity - enteredNum;
        setbuy(prevBuy => ({ ...prevBuy, Quantity: updatedQuantity }));
        settotaldata([...totaldata, buy]);
    };

    const handleDel = () => {
        // Implementation for deleting an item
    };

    const dataArray = Object.keys(props).map(key => ({ name: key, value: props[key] }));

    return (
        <>
            {dataArray.map((item, i) => (
                <form key={i}>
                    <p>{item.name}</p>
                    <p>Value: {item.value}</p>
                    <label htmlFor="in">Enter Number:</label>
                    <input type='number' id='in'></input>
                    <button type='button' onClick={handleBuy}>Buy</button>
                    <button type='button' onClick={handleDel}>Delete</button>
                </form>
            ))}
        </>
    );
};

export default ShowOnscreen;
