import { useState } from "react"
import ShowOnscreen from "./ShowOnScreen"
const [entereddata,setentereddata]=['']
const Form=()=>{
    const handleFormSubmit=(event)=>{
        event.preventDefault()
        // setentereddata(event)
        const form_data = {
            Name:event.target.element.name.value,
            Price: event.target.element.name.value,
            Quantity:event.target.element.name.value

        }
        setentereddata(form_data)

    }
    return(
        <div>

        
            <form onSubmit={handleFormSubmit}>        
                <label htmlFor="name">Name</label>
                <input type='text' id='name'></input>
                <label htmlFor="price">Price</label>
                <input type='number' id='price'></input>
                <label htmlFor="quantity">Quantity</label>
                <input type="number" id='quantity'></input>
                <button type='submit'>Add to Shop</button>
            </form>
            <ShowOnscreen props={entereddata}/>
        </div>
    )
}
export default Form
